INSERT INTO `Domains` VALUES (1,1,'Evaluate, Direct and Monitor'),(2,2,'Align, Plan and Organize'),(2,3,'Build, Acquire and Implement'),(2,4,'Deliver, Service and Support'),(2,5,'Monitor, Evaluate and Assess');
